import {drawAxes, drawSqrt} from './graph'
import forestboy from './ForestBoy.png'
import {HERO, ENEMY1} from './classes'

class Sprite {
	constructor(img){
		this.img = img
		this.currentFrame = 0
		this.currentState
		this.w = 16
		this.h = 24
	}

	draw(ctx, state, pos){

		if(this.currentState != state){
			this.currentState = state
			this.currentFrame = 0
		}

		let n = Math.floor(this.currentFrame / 30)
		const {w,h} = this
		const srcX = 8 + n*w
		const srcY = 145
		if (false){ 
			ctx.save();
	    	ctx.scale(-1, 1);
			ctx.drawImage(this.img, srcX, srcY, w, h, 
			 	-pos.x, 
			 	pos.y - h , -w, h);

			ctx.restore();
		} else {
			ctx.drawImage(this.img, srcX, srcY, w, h, 
			 	pos.x, 
			 	pos.y - h , w, h);
		}


		this.currentFrame++;
		this.currentFrame %= 180
	}
}

const sprites = new Map()

let ctx;

export function initRender(){

	const canvas = document.getElementById('tututu');
	ctx = canvas.getContext('2d');
	ctx.fillStyle = 'rgba(0, 0, 200, 0.5)'
}


export function loadAssets(state){

	const heroImage = new Image();
	heroImage.src = forestboy;
	const heroSprite = new Sprite(heroImage);

	sprites.set(HERO, heroSprite)


}

export function coords (ctx, hero){

	ctx.font = '15px arial'
	ctx.fillText(Math.round(hero.pos.x)+':'+Math.round(hero.pos.y), 350 ,470 );


}


export function render(state) {


	ctx.clearRect(0,0, ctx.canvas.width, ctx.canvas.height);

	coords(ctx, state.hero);

  	drawAxes(ctx);
	drawSqrt(ctx);

	for (let i = 0; i < state.lvl.length; i++) {

		let p = state.lvl[i]
		//ctx.fillStyle = 'rgb(200, 150, 150)'
		ctx.fillStyle = 'lwn'
		ctx.fillRect(p.pos.x, p.pos.y, p.w, 50);


	}

	//-- hero -------------
	sprites.get(HERO).draw(ctx, state.hero.state, state.hero.pos);

}
